import "./exprenseItem.css";

function ExpenseItem (){
    const name = "Anastasia";
    const date= new Date();

    const options={
        weekday: 'long', 
        year: 'numeric', 
        month: 'long', 
        day: 'numeric'
    };
    const currentDay = date.toLocaleString('en',options)
    return <div className="expense-item ">
        <h2>Hello {name}!</h2> 
        <div>{currentDay}</div>
        <br/>
        <div className="expense-item__description">
            <p><strong>Car Insurance</strong></p>
            <div className="expense-item__price ">$216</div>
            </div>
        
        </div> 
};
export default ExpenseItem ;
